import { Container, Stack, Box, Typography, TextField, Button } from '@mui/material';
import React, { useState } from 'react';


function Copyright() {
    return (
      <Typography variant="body2" color="text.secondary" align="center">
        {'Copyright © '}
        The Chan Gang{' '}
        {new Date().getFullYear()}
        {'.'}
      </Typography>
    );
}

const Main = () => {
    const [repoUrl, setRepoUrl] = useState("");
    const [attemptSearch, setAttemptSearch] = useState(false);
    const hasError = attemptSearch && (repoUrl.length < 1);
    return (
      <Container maxWidth="sm">
        <Stack justifyContent={"center"} alignItems={"center"} minHeight={"100vh"}>
          <Box display="block">
          <Typography variant="h1" component="h1" sx={{ mb: 2 }}>
            Code Query
          </Typography>
          </Box>
          <TextField
            value={repoUrl}
            onChange={(event) => {
              setRepoUrl(event.target.value);
            }}
            sx={{ width: "50vw", height: "10vh"}} 
            id="repo-url" 
            label="Repo URL" 
            variant="standard"
            error={hasError}
            helperText={hasError && "Please enter a Github repository URL."}
            />
          <Button sx={{ my: 2}} variant="outlined" onSubmit={() => setAttemptSearch(true)}>Search</Button>
          <Copyright />
        </Stack>
      </Container>
    );
}

export default Main;
